import React, {useState}  from 'react'
export default function TextForm(props) {
  const [text,setText]=useState('Enter text here');
  
 const handleUpClick =()=>{
    console.log(text);
    console.log("Uppercase was clicked");
     setText(text);
  }
  const handleOnChange=(event)=>{
    console.log("On Change");
    setText(event.target.value);
  }
  console.log(text);
  return(
      <div className="container my-4" >
<div className="mb-3 " >
  <label htmlFor="textarea" className="form-label">{props.topic}</label>
  <textarea className="form-control" onChange={handleOnChange} value={text} id="textarea" rows="8"></textarea>
 <button className="btn btn-danger" onClick={handleUpClick}>Convert to UpperCase</button>
</div>       
</div>  );
}
